import React from 'react';
import ProductCard from './ProductCard';

const Home = ({ products, onAddToCart }) => (
  <>
    <style>{`
      .hero-section {
        background: linear-gradient(120deg, #6366f1 0%, #60a5fa 100%);
        color: white;
        padding: 72px 0 56px 0;
        text-align: center;
        position: relative;
        overflow: hidden;
        box-shadow: 0 8px 32px 0 rgba(99,102,241,0.15);
      }
      .hero-illustration {
        position: absolute;
        right: 8vw;
        top: 30px;
        width: 180px;
        opacity: 0.18;
        z-index: 0;
        pointer-events: none;
        filter: blur(0.5px);
      }
      .hero-section h1 {
        font-size: 3.2rem;
        font-weight: 900;
        margin-bottom: 1.2rem;
