        border-radius: 999px;
        box-shadow: 0 2px 8px rgba(244,63,94,0.10);
        letter-spacing: 1px;
      }
      .product-title {
        font-size: 1.1rem;
        font-weight: 700;
        color: #222;
        margin-bottom: 0.5rem;
        text-align: center;
        min-height: 2.5em;
      }
      .product-desc {
        font-size: 0.98rem;
        color: #666;
        margin-bottom: 0.7rem;
        text-align: center;
        min-height: 2.2em;
      }
      .product-price-row {
        display: flex;
        align-items: center;
        gap: 0.7rem;
        margin-bottom: 1.1rem;
        justify-content: center;
      }
      .product-price {
        font-size: 1.25rem;
        font-weight: 800;
        color: #6366f1;
      }
      .product-oldprice {
        font-size: 1rem;
        color: #bbb;
        text-decoration: line-through;
        font-weight: 500;
      }
      .add-to-cart-btn {
        background: linear-gradient(90deg, #60a5fa 0%, #6366f1 100%);
        color: #fff;
        font-weight: 700;
        padding: 0.7rem 2.2rem;
        border-radius: 999px;
        font-size: 1.05rem;
        border: none;
        box-shadow: 0 2px 12px rgba(99,102,241,0.10);
        cursor: pointer;
        transition: background 0.2s, transform 0.2s;
