import React from 'react';
import { Link } from 'react-router-dom';

function Header({ cart, removeFromCart, updateQuantity }) {
  return (
    <header style={{
      background: 'linear-gradient(90deg, #6366f1 0%, #60a5fa 100%)',
      color: '#fff',
      boxShadow: '0 4px 32px 0 rgba(99,102,241,0.10)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      height: '4rem'
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '1.2rem 2rem',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between'
      }}>
        <Link to="/" style={{
          fontSize: '2rem',
          fontWeight: 900,
          letterSpacing: '-1px',
          color: '#fbbf24',
          textDecoration: 'none',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem'
        }}>
          <i className="fas fa-store"></i> 
        </Link>
        <nav style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
          <Link to="/cart" style={{ color: '#fff', textDecoration: 'none', fontWeight: 500 }}>
            <i className="fas fa-shopping-cart"></i> Giỏ hàng ({cart.length})
          </Link>
        </nav>
      </div>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css" />
    </header>
  );
}

export default Header;
