import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Home = ({ products, onAddToCart, cart, isAuthenticated, user, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();



  return (
    <>
      <style>{`
        :root {
          --primary: #6366f1;
          --primary-dark: #3730a3;
          --accent: #fbbf24;
          --white: #fff;
          --shadow: 0 4px 32px 0 rgba(99,102,241,0.10);
        }
        .header-main {
          background: linear-gradient(90deg, #6366f1 0%, #60a5fa 100%);
          color: #fff;
          box-shadow: var(--shadow);
          position: sticky;
          top: 0;
          z-index: 1000;
        }
        .header-content {
          max-width: 1200px;
          margin: 0 auto;
          padding: 1.2rem 2rem;
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .logo {
          font-size: 2rem;
          font-weight: 900;
          letter-spacing: -1px;
          color: var(--accent);
          text-decoration: none;
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }
        .search-bar {
          flex: 1;
          max-width: 420px;
          margin: 0 2rem;
          position: relative;
        }
        .search-bar input {
          width: 100%;
          padding: 0.8rem 1rem 0.8rem 2.5rem;
          border-radius: 999px;
          border: none;
          font-size: 1rem;
          background: #f3f4f6;
          color: #222;
          box-shadow: 0 2px 8px rgba(99,102,241,0.07);
        }
        .search-bar input:focus {
          outline: 2px solid var(--accent);
        }
        .search-bar .fa-search {
          position: absolute;
          left: 1rem;
          top: 50%;
          transform: translateY(-50%);
          color: #a5b4fc;
          font-size: 1.1rem;
        }
        .header-nav {
          display: flex;
          align-items: center;
          gap: 1.5rem;
        }
        .header-link {
          color: #fff;
          text-decoration: none;
          font-weight: 500;
          font-size: 1.1rem;
          display: flex;
          align-items: center;
          gap: 0.5rem;
          position: relative;
          transition: color 0.2s;
        }
        .header-link:hover {
          color: var(--accent);
        }
        .cart-badge {
          position: absolute;
          top: -8px;
          right: -12px;
          background: var(--accent);
          color: #222;
          border-radius: 50%;
          width: 20px;
          height: 20px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 0.8rem;
          font-weight: 700;
          box-shadow: 0 2px 8px rgba(251,191,36,0.15);
        }
        .user-menu {
          position: relative;
        }
        .user-btn {
          background: none;
          border: none;
          color: #fff;
          font-size: 1.1rem;
          display: flex;
          align-items: center;
          gap: 0.5rem;
          cursor: pointer;
          border-radius: 8px;
          padding: 0.5rem 1rem;
          transition: background 0.2s;
        }
        .user-btn:hover {
          background: rgba(255,255,255,0.08);
        }
        .user-dropdown {
          position: absolute;
          top: 120%;
          right: 0;
          background: #fff;
          color: #222;
          border-radius: 12px;
          box-shadow: 0 8px 32px rgba(99,102,241,0.13);
          min-width: 180px;
          z-index: 10;
          padding: 0.5rem 0;
        }
        .user-dropdown a, .user-dropdown button {
          display: block;
          width: 100%;
          padding: 0.8rem 1.2rem;
          background: none;
          border: none;
          color: #222;
          text-align: left;
          font-size: 1rem;
          cursor: pointer;
          text-decoration: none;
          transition: background 0.2s, color 0.2s;
        }
        .user-dropdown a:hover, .user-dropdown button:hover {
          background: #f3f4f6;
          color: var(--primary);
        }
        .hero-section {
          background: linear-gradient(120deg, #6366f1 0%, #60a5fa 100%);
          color: white;
          padding: 72px 0 56px 0;
          text-align: center;
          position: relative;
          overflow: hidden;
          box-shadow: 0 8px 32px 0 rgba(99,102,241,0.15);
        }
        .hero-illustration {
          position: absolute;
          right: 8vw;
          top: 30px;
          width: 180px;
          opacity: 0.18;
          z-index: 0;
          pointer-events: none;
          filter: blur(0.5px);
        }
        .hero-section h1 {
          font-size: 3.2rem;
          font-weight: 900;
          margin-bottom: 1.2rem;
          letter-spacing: -1.5px;
          text-shadow: 0 2px 16px rgba(0,0,0,0.08);
          z-index: 1;
          position: relative;
        }
        .hero-section p {
          font-size: 1.35rem;
          margin-bottom: 2.2rem;
          opacity: 0.97;
          z-index: 1;
          position: relative;
        }
        .hero-btn {
          background: linear-gradient(90deg, #fbbf24 0%, #f59e42 100%);
          color: #222;
          font-weight: 800;
          padding: 1rem 2.8rem;
          border-radius: 999px;
          font-size: 1.2rem;
          border: none;
          box-shadow: 0 6px 32px rgba(251,191,36,0.18);
          cursor: pointer;
          transition: background 0.2s, transform 0.2s, box-shadow 0.2s;
          z-index: 1;
          position: relative;
        }
        .hero-btn:hover {
          background: linear-gradient(90deg, #f59e42 0%, #fbbf24 100%);
          transform: translateY(-3px) scale(1.06);
          box-shadow: 0 10px 40px rgba(251,191,36,0.25);
        }
        .products-section {
          max-width: 1200px;
          margin: 0 auto;
          padding: 56px 16px 40px 16px;
        }
        .products-title {
          font-size: 2.2rem;
          font-weight: 800;
          margin-bottom: 2.5rem;
          color: #222;
          text-align: center;
          letter-spacing: -1px;
        }
        .products-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(270px, 1fr));
          gap: 2.5rem;
        }
        .product-card-custom {
          background: #fff;
          border-radius: 1.5rem;
          box-shadow: 0 4px 32px 0 rgba(99,102,241,0.10);
          padding: 1.5rem 1.2rem 1.2rem 1.2rem;
          display: flex;
          flex-direction: column;
          align-items: center;
          transition: box-shadow 0.2s, transform 0.2s;
          position: relative;
          overflow: hidden;
        }
        .product-card-custom:hover {
          box-shadow: 0 8px 40px 0 rgba(99,102,241,0.18);
          transform: translateY(-6px) scale(1.03);
          z-index: 2;
        }
        .product-image-wrap {
          width: 120px;
          height: 120px;
          border-radius: 1rem;
          overflow: hidden;
          margin-bottom: 1.2rem;
          background: #f3f4f6;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .product-image-wrap img {
          width: 100%;
          height: 100%;
          object-fit: contain;
          transition: transform 0.2s;
        }
        .product-card-custom:hover .product-image-wrap img {
          transform: scale(1.08) rotate(-2deg);
        }
        .product-badge {
          position: absolute;
          top: 18px;
          left: 18px;
          background: linear-gradient(90deg, #f43f5e 0%, #fbbf24 100%);
          color: #fff;
          font-size: 0.85rem;
          font-weight: 700;
          padding: 0.3rem 1rem;
          border-radius: 999px;
          box-shadow: 0 2px 8px rgba(244,63,94,0.10);
          letter-spacing: 1px;
        }
        .product-title {
          font-size: 1.1rem;
          font-weight: 700;
          color: #222;
          margin-bottom: 0.5rem;
          text-align: center;
          min-height: 2.5em;
        }
        .product-desc {
          font-size: 0.98rem;
          color: #666;
          margin-bottom: 0.7rem;
          text-align: center;
          min-height: 2.2em;
        }
        .product-price-row {
          display: flex;
          align-items: center;
          gap: 0.7rem;
          margin-bottom: 1.1rem;
          justify-content: center;
        }
        .product-price {
          font-size: 1.25rem;
          font-weight: 800;
          color: #6366f1;
        }
        .product-oldprice {
          font-size: 1rem;
          color: #bbb;
          text-decoration: line-through;
          font-weight: 500;
        }
        .add-to-cart-btn {
          background: linear-gradient(90deg, #60a5fa 0%, #6366f1 100%);
          color: #fff;
          font-weight: 700;
          padding: 0.7rem 2.2rem;
          border-radius: 999px;
          font-size: 1.05rem;
          border: none;
          box-shadow: 0 2px 12px rgba(99,102,241,0.10);
          cursor: pointer;
          transition: background 0.2s, transform 0.2s;
        }
        .add-to-cart-btn:hover {
          background: linear-gradient(90deg, #6366f1 0%, #60a5fa 100%);
          transform: scale(1.06);
        }
        .footer-section {
          background: linear-gradient(90deg, #6366f1 0%, #60a5fa 100%);
          color: white;
          padding: 48px 0 24px 0;
          text-align: center;
          margin-top: 48px;
          position: relative;
        }
        .footer-section .footer-title {
          font-size: 1.5rem;
          font-weight: 700;
          margin-bottom: 1rem;
        }
        .footer-links {
          display: flex;
          justify-content: center;
          gap: 2rem;
          margin-bottom: 1.5rem;
          flex-wrap: wrap;
        }
        .footer-link {
          color: #fbbf24;
          text-decoration: none;
          font-weight: 500;
          transition: color 0.2s;
        }
        .footer-link:hover {
          color: #fff;
          text-decoration: underline;
        }
        .footer-socials {
          margin-bottom: 1.5rem;
        }
        .footer-socials a {
          display: inline-block;
          margin: 0 0.5rem;
          color: #fff;
          font-size: 1.5rem;
          transition: color 0.2s, transform 0.2s;
        }
        .footer-socials a:hover {
          color: #fbbf24;
          transform: scale(1.15);
        }
        .footer-copyright {
          font-size: 0.95rem;
          opacity: 0.8;
        }
        @media (max-width: 600px) {
          .hero-section h1 { font-size: 2rem; }
          .products-title { font-size: 1.3rem; }
          .footer-section .footer-title { font-size: 1.1rem; }
          .products-grid { gap: 1.2rem; }
          .product-card-custom { padding: 1rem 0.5rem; }
        }
      `}</style>
      {/* HERO SECTION */}
      <section className="hero-section" style={{
        background: 'radial-gradient(circle at 60% 40%, #a5b4fc 0%, #6366f1 60%, #312e81 100%)',
        color: 'white',
        padding: '90px 0 70px 0',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden',
        boxShadow: '0 8px 32px 0 rgba(99,102,241,0.18)'
      }}>
        <svg style={{
          position: 'absolute', left: 0, top: 0, width: '100%', height: '100%', zIndex: 0, pointerEvents: 'none'
        }}>
          <circle cx="80%" cy="20%" r="120" fill="#fbbf24" fillOpacity="0.13"/>
          <circle cx="20%" cy="80%" r="90" fill="#fff" fillOpacity="0.08"/>
        </svg>
        <div style={{ position: 'relative', zIndex: 1 }}>
          <img src="https://cdn-icons-png.flaticon.com/512/1170/1170576.png" alt="hero" style={{
            width: 110, marginBottom: 24, filter: 'drop-shadow(0 8px 32px #6366f1aa)'
          }}/>
          <h1 style={{
            fontSize: '3.2rem', fontWeight: 900, marginBottom: 16, letterSpacing: '-2px', textShadow: '0 2px 16px #312e81'
          }}>
            <span style={{ color: '#fbbf24' }}>Siêu Thị Online</span> <br/>Mua sắm cực chất!
          </h1>
          <p style={{
            fontSize: '1.35rem', marginBottom: 32, opacity: 0.97, fontWeight: 500
          }}>
            Ưu đãi mỗi ngày, giao hàng siêu tốc, sản phẩm công nghệ chính hãng, giá cực tốt!
          </p>
          <a href="#products" style={{
            background: 'linear-gradient(90deg, #fbbf24 0%, #f59e42 100%)',
            color: '#222', fontWeight: 800, padding: '1.1rem 3rem', borderRadius: 999,
            fontSize: '1.25rem', border: 'none', boxShadow: '0 6px 32px #fbbf2440',
            cursor: 'pointer', textDecoration: 'none', transition: 'all 0.2s'
          }}>
            <i className="fas fa-bolt" style={{ marginRight: 10, color: '#f59e42' }}></i>
            Khám phá ngay
          </a>
        </div>
      </section>

      {/* PRODUCTS */}
      <section className="products-section" id="products">
        <div className="products-title">Sản phẩm nổi bật</div>
        <div className="products-grid">
          {products.map(product => (
            <Link to={`/product/${product.id}`} className="product-card-custom" key={product.id} style={{ textDecoration: 'none' }}>
              {product.rating && product.rating.rate > 4.2 && (
                <div className="product-badge">HOT</div>
              )}
              <div className="product-image-wrap">
                <img src={product.image} alt={product.title} />
              </div>
              <div className="product-title">{product.title}</div>
              <div className="product-desc">{product.category}</div>
              <div className="product-price-row">
                <span className="product-price">{product.price.toLocaleString('vi-VN', {style:'currency',currency:'VND'})}</span>
                {product.oldPrice && (
                  <span className="product-oldprice">{product.oldPrice.toLocaleString('vi-VN', {style:'currency',currency:'VND'})}</span>
                )}
              </div>
              <button className="add-to-cart-btn" onClick={e => { e.preventDefault(); onAddToCart(product); }}>
                <i className="fas fa-cart-plus" style={{marginRight:8}}></i>Thêm vào giỏ
              </button>
            </Link>
          ))}
        </div>
      </section>
    </>
  );
};

export default Home;
