import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Header from './components/Header';
import Hero from './components/Hero';
import Footer from './components/Footer';
import ProductCard from './components/ProductCard';
import ProductDetail from './components/ProductDetail';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Auth from './components/Auth';
import Account from './components/Account';
import Orders from './components/Orders';
import Search from './components/Search';
import Review from './components/Review';
import Admin from './components/Admin';

// Global styles
const globalStyles = `
  @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;500;700&display=swap');

  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Roboto', sans-serif;
  }

  :root {
    --primary-color: #2563eb;
    --primary-hover: #1d4ed8;
    --secondary-color: #10b981;
    --secondary-hover: #059669;
    --text-color: #1f2937;
    --text-light: #6b7280;
    --background-light: #f3f4f6;
    --white: #ffffff;
    --danger: #ef4444;
    --danger-hover: #dc2626;
    --success: #22c55e;
    --success-hover: #16a34a;
    --border-color: #e5e7eb;
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
    --gradient-primary: linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%);
    --gradient-danger: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
    --gradient-success: linear-gradient(135deg, #10b981 0%, #059669 100%);
  }

  body {
    background: linear-gradient(135deg, #f0f4ff 0%, #e0e7ff 100%);
    min-height: 100vh;
    color: var(--text-color);
    line-height: 1.5;
    position: relative;
    overflow-x: hidden;
  }

  /* Decorative blurred circles for full-page background */
  body::before, body::after {
    content: '';
    position: fixed;
    z-index: 0;
    border-radius: 50%;
    pointer-events: none;
    opacity: 0.5;
    filter: blur(80px);
    transition: opacity 0.3s;
  }
  body::before {
    width: 400px;
    height: 400px;
    top: -120px;
    left: -120px;
    background: radial-gradient(circle, #a5b4fc 0%, #f0f4ff 80%);
  }
  body::after {
    width: 500px;
    height: 500px;
    bottom: -180px;
    right: -180px;
    background: radial-gradient(circle, #818cf8 0%, #e0e7ff 80%);
  }

  h1, h2, h3, h4, h5, h6 {
    font-weight: 600;
    line-height: 1.25;
  }

  button, input, select, textarea {
    font-family: inherit;
  }

  a {
    color: var(--primary-color);
    text-decoration: none;
    transition: all 0.2s ease;
  }

  a:hover {
    color: var(--primary-hover);
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes slideIn {
    from { transform: translateX(-20px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }

  @keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.05); }
    100% { transform: scale(1); }
  }

  .section-title {
    position: relative;
    display: inline-block;
    margin-bottom: 2rem;
  }

  .section-title::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 0;
    width: 60px;
    height: 4px;
    background: var(--gradient-primary);
    border-radius: 2px;
  }

  .card {
    background: var(--white);
    border-radius: 12px;
    box-shadow: var(--shadow);
    transition: all 0.3s ease;
  }

  .card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-lg);
  }

  .badge {
    position: absolute;
    top: 12px;
    right: 12px;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 0.75rem;
    font-weight: 500;
    color: var(--white);
    z-index: 1;
  }

  .badge-new {
    background: var(--gradient-success);
  }

  .badge-sale {
    background: var(--gradient-danger);
  }

  .price {
    display: flex;
    align-items: baseline;
    gap: 0.5rem;
  }

  .price-old {
    color: var(--text-light);
    text-decoration: line-through;
    font-size: 0.875rem;
  }

  .price-new {
    color: var(--danger);
    font-weight: 600;
    font-size: 1.25rem;
  }

  .discount {
    background: var(--gradient-danger);
    color: var(--white);
    padding: 2px 6px;
    border-radius: 4px;
    font-size: 0.75rem;
    font-weight: 500;
  }

  /* Responsive grid for product cards */
  @media (max-width: 1200px) {
    .product-grid {
      grid-template-columns: repeat(3, 1fr);
    }
  }
  @media (max-width: 900px) {
    .product-grid {
      grid-template-columns: repeat(2, 1fr);
    }
  }
  @media (max-width: 600px) {
    .product-grid {
      grid-template-columns: 1fr;
    }
  }
`;

// Protected Route component
const ProtectedRoute = ({ children, allowedRoles }) => {
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
  const userRole = localStorage.getItem('userRole');

  if (!isLoggedIn) {
    return <Navigate to="/auth" />;
  }

  if (allowedRoles && !allowedRoles.includes(userRole)) {
    return <Navigate to="/" />;
  }

  return children;
};

const products = [
  {
    id: 1,
    name: 'Điện thoại iPhone 13',
    description: 'iPhone 13 128GB - Hàng chính hãng',
    price: 19990000,
    oldPrice: 22990000,
    image: 'https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-blue-1-600x600.jpg',
    category: 'Điện thoại',
    isNew: true,
    isSale: true
  },
  {
    id: 2,
    name: 'Laptop Dell XPS 13',
    description: 'Laptop Dell XPS 13 9310 - Core i7',
    price: 32990000,
    oldPrice: 35990000,
    image: 'https://images.fpt.shop/unsafe/fit-in/800x800/filters:quality(90):fill(white)/fptshop.com.vn/Uploads/Originals/2021/9/15/637673978199600673_dell-xps-13-9310-i7-1165g7-16gb-512gb-4k-touch-win10-bac-1.png',
    category: 'Laptop',
    isNew: true,
    isSale: true
  },
  {
    id: 3,
    name: 'Tai nghe AirPods Pro',
    description: 'Tai nghe Apple AirPods Pro - Hàng chính hãng',
    price: 5990000,
    oldPrice: 6990000,
    image: 'https://cdn.tgdd.vn/Products/Images/54/211032/airpods-pro-2-thumb-600x600.jpg',
    category: 'Phụ kiện',
    isNew: false,
    isSale: true
  },
  {
    id: 4,
    name: 'Samsung Galaxy S21',
    description: 'Samsung Galaxy S21 5G 128GB',
    price: 16990000,
    oldPrice: 19990000,
    image: 'https://cdn.tgdd.vn/Products/Images/42/226316/samsung-galaxy-s21-tim-600x600.jpg',
    category: 'Điện thoại',
    isNew: false,
    isSale: true
  },
  {
    id: 5,
    name: 'MacBook Pro M1',
    description: 'MacBook Pro M1 13 inch 256GB',
    price: 27990000,
    oldPrice: 29990000,
    image: 'https://cdn.tgdd.vn/Products/Images/44/231244/macbook-pro-m1-2020-16gb-600x600.jpg',
    category: 'Laptop',
    isNew: true,
    isSale: false
  },
  {
    id: 6,
    name: 'Apple Watch Series 7',
    description: 'Apple Watch Series 7 GPS 45mm',
    price: 10990000,
    oldPrice: 12990000,
    image: 'https://cdn.tgdd.vn/Products/Images/7077/250728/apple-watch-s7-45mm-midnight-aluminum-midnight-sport-band-600x600.jpg',
    category: 'Phụ kiện',
    isNew: true,
    isSale: true
  }
];

const categories = [
  {
    id: 1,
    name: 'Điện thoại',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-phone.png',
    count: 2
  },
  {
    id: 2,
    name: 'Laptop',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-laptop.png',
    count: 2
  },
  {
    id: 3,
    name: 'Phụ kiện',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-tablet.png',
    count: 2
  }
];

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);

  // Hàm kiểm tra trạng thái đăng nhập
  const checkLoginStatus = () => {
    const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
    const role = localStorage.getItem('userRole');
    setIsLoggedIn(loggedIn);
    setUserRole(role);
  };

  // Kiểm tra trạng thái đăng nhập khi component mount
  useEffect(() => {
    checkLoginStatus();
  }, []);

  // Lắng nghe sự kiện storage để cập nhật trạng thái đăng nhập
  useEffect(() => {
    const handleStorageChange = () => {
      checkLoginStatus();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const addToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
      setCartItems(cartItems.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCartItems(cartItems.filter(item => item.id !== productId));
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Router>
      <style>{globalStyles}</style>
      <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <Header 
          cartCount={cartItems.length} 
