    id: 2,
    name: 'Laptop',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-laptop.png',
    count: 2
  },
  {
    id: 3,
    name: 'Phụ kiện',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-tablet.png',
    count: 2
  }
];

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);

  // Check login status
  const checkLoginStatus = () => {
    const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
    const role = localStorage.getItem('userRole');
    setIsLoggedIn(loggedIn);
    setUserRole(role);
  };

  useEffect(() => {
    checkLoginStatus();
  }, []);

  useEffect(() => {
    const handleStorageChange = () => {
      checkLoginStatus();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const addToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
      setCartItems(cartItems.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCartItems(cartItems.filter(item => item.id !== productId));
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Router>
      <div className="app">
        <Header 
          cartCount={cartItems.length} 
          isLoggedIn={isLoggedIn} 
          userRole={userRole}
          onLogout={checkLoginStatus} 
        />
        <main>
          <Routes>
            {/* Public routes */}
            <Route path="/auth" element={<Auth onLogin={checkLoginStatus} />} />
            <Route path="/search" element={<Search products={products} />} />
            <Route path="/" element={
              <>
                <Hero />
                <div className="container">
                  {/* Flash Sale Section */}
                  <section className="flash-sale">
                    <div className="flash-sale-content">
                      <h2>Flash Sale</h2>
                      <div className="countdown">
                        {[23, 59, 59].map((num, index) => (
                          <div key={index} className="countdown-item">
                            {num}
                          </div>
                        ))}
                      </div>
                      <Link to="/search?type=sale" className="btn-view-all">
                        Xem tất cả
                      </Link>
                    </div>
                  </section>

                  {/* Categories Section */}
                  <section className="categories">
                    <h2 className="section-title">Danh Mục Sản Phẩm</h2>
                    <div className="category-grid">
                      {categories.map(category => (
                        <Link
                          key={category.id}
                          to={`/search?category=${category.name}`}
                          className="category-card"
                        >
                          <div className="category-icon">
                            <img src={category.image} alt={category.name} />
                          </div>
                          <h3>{category.name}</h3>
                          <p>{category.count} sản phẩm</p>
                        </Link>
                      ))}
                    </div>
                  </section>

                  {/* New Products Section */}
                  <section className="new-products">
                    <div className="section-header">
                      <h2 className="section-title">Sản Phẩm Mới</h2>
                      <Link to="/search?type=new" className="btn-view-all">
                        Xem tất cả
                      </Link>
                    </div>
                    <div className="product-grid">
                      {products.filter(p => p.isNew).map(product => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          addToCart={addToCart}
                        />
                      ))}
                    </div>
                  </section>

                  {/* Featured Products Section */}
                  <section className="featured-products">
                    <div className="section-header">
                      <h2 className="section-title">Sản Phẩm Nổi Bật</h2>
                      <Link to="/search" className="btn-view-all">
                        Xem tất cả
                      </Link>
                    </div>
                    <div className="product-grid">
                      {products.map(product => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          addToCart={addToCart}
                        />
                      ))}
                    </div>
                  </section>
                </div>
              </>
            } />

            {/* Protected user routes */}
            <Route 
              path="/product/:id" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <ProductDetail products={products} addToCart={addToCart} />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/cart" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/checkout" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Checkout cartItems={cartItems} total={total} />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/account" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Account />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/orders" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Orders />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/product/:id/reviews" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Review />
                </ProtectedRoute>
              } 
            />

            {/* Admin routes */}
            <Route 
              path="/admin" 
              element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <Admin />
                </ProtectedRoute>
              } 
            />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
