import React from 'react';
import { Link } from 'react-router-dom';
import { FaFacebook, FaTwitter, FaInstagram, FaYoutube, FaPhone, FaEnvelope, FaMapMarkerAlt } from 'react-icons/fa';

function Footer() {
  return (
    <footer style={{
      backgroundColor: '#1a1a1a',
      color: 'white',
      padding: '4rem 2rem 2rem',
      position: 'relative'
    }}>
      {/* Top wave decoration */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '50px',
        background: 'linear-gradient(135deg, #2196F3, #4CAF50)',
        clipPath: 'polygon(0 0, 100% 0, 100% 100%, 0 0)'
      }} />

      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        display: 'grid',
        gridTemplateColumns: 'repeat(4, 1fr)',
        gap: '3rem'
      }}>
        {/* Company Info */}
        <div>
          <h3 style={{
            fontSize: '1.5rem',
            marginBottom: '1.5rem',
            color: '#4CAF50'
          }}>
            Shop Online
          </h3>
          <p style={{
            color: '#999',
            lineHeight: '1.6',
            marginBottom: '1.5rem'
          }}>
            Chúng tôi cam kết mang đến cho bạn trải nghiệm mua sắm trực tuyến tốt nhất với hàng ngàn sản phẩm chất lượng.
          </p>
          <div style={{
            display: 'flex',
            gap: '1rem'
          }}>
            <a href="#" style={{
              color: 'white',
              fontSize: '1.5rem',
              transition: 'all 0.3s ease',
              '&:hover': {
                color: '#4CAF50',
                transform: 'translateY(-3px)'
              }
            }}>
              <FaFacebook />
            </a>
            <a href="#" style={{
              color: 'white',
              fontSize: '1.5rem',
              transition: 'all 0.3s ease',
              '&:hover': {
                color: '#4CAF50',
                transform: 'translateY(-3px)'
              }
            }}>
              <FaTwitter />
            </a>
            <a href="#" style={{
              color: 'white',
              fontSize: '1.5rem',
              transition: 'all 0.3s ease',
              '&:hover': {
                color: '#4CAF50',
                transform: 'translateY(-3px)'
              }
            }}>
              <FaInstagram />
            </a>
            <a href="#" style={{
              color: 'white',
              fontSize: '1.5rem',
              transition: 'all 0.3s ease',
              '&:hover': {
                color: '#4CAF50',
                transform: 'translateY(-3px)'
              }
            }}>
              <FaYoutube />
            </a>
          </div>
        </div>

        {/* Quick Links */}
        <div>
          <h4 style={{
            fontSize: '1.2rem',
            marginBottom: '1.5rem',
            color: '#4CAF50'
          }}>
            Liên Kết Nhanh
          </h4>
          <ul style={{
            listStyle: 'none',
            padding: 0,
            margin: 0
          }}>
            <li style={{ marginBottom: '0.8rem' }}>
              <Link to="/about" style={{
                color: '#999',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                display: 'inline-block',
                '&:hover': {
                  color: '#4CAF50',
                  transform: 'translateX(5px)'
                }
              }}>
                Về Chúng Tôi
              </Link>
            </li>
            <li style={{ marginBottom: '0.8rem' }}>
              <Link to="/products" style={{
                color: '#999',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                display: 'inline-block',
                '&:hover': {
                  color: '#4CAF50',
                  transform: 'translateX(5px)'
                }
              }}>
                Sản Phẩm
              </Link>
            </li>
            <li style={{ marginBottom: '0.8rem' }}>
              <Link to="/blog" style={{
                color: '#999',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                display: 'inline-block',
                '&:hover': {
                  color: '#4CAF50',
                  transform: 'translateX(5px)'
                }
              }}>
                Blog
              </Link>
            </li>
            <li style={{ marginBottom: '0.8rem' }}>
              <Link to="/contact" style={{
                color: '#999',
                textDecoration: 'none',
                transition: 'all 0.3s ease',
                display: 'inline-block',
                '&:hover': {
                  color: '#4CAF50',
                  transform: 'translateX(5px)'
                }
              }}>
                Liên Hệ
              </Link>
            </li>
          </ul>
        </div>

        {/* Contact Info */}
        <div>
          <h4 style={{
            fontSize: '1.2rem',
            marginBottom: '1.5rem',
            color: '#4CAF50'
          }}>
            Thông Tin Liên Hệ
          </h4>
          <ul style={{
            listStyle: 'none',
            padding: 0,
            margin: 0
          }}>
            <li style={{
              display: 'flex',
              alignItems: 'center',
              gap: '1rem',
              marginBottom: '1rem',
              color: '#999'
            }}>
              <FaPhone style={{ color: '#4CAF50' }} />
              <span>+84 123 456 789</span>
            </li>
            <li style={{
              display: 'flex',
              alignItems: 'center',
              gap: '1rem',
              marginBottom: '1rem',
              color: '#999'
            }}>
              <FaEnvelope style={{ color: '#4CAF50' }} />
              <span>contact@shoponline.com</span>
            </li>
            <li style={{
              display: 'flex',
              alignItems: 'center',
              gap: '1rem',
              marginBottom: '1rem',
              color: '#999'
            }}>
              <FaMapMarkerAlt style={{ color: '#4CAF50' }} />
              <span>123 Đường ABC, Quận XYZ, TP.HCM</span>
            </li>
          </ul>
        </div>

        {/* Newsletter */}
        <div>
          <h4 style={{
            fontSize: '1.2rem',
            marginBottom: '1.5rem',
            color: '#4CAF50'
          }}>
            Đăng Ký Nhận Tin
          </h4>
          <p style={{
            color: '#999',
            marginBottom: '1rem'
          }}>
            Nhận thông tin về sản phẩm mới và khuyến mãi đặc biệt
          </p>
          <form style={{
            display: 'flex',
            gap: '0.5rem'
          }}>
            <input
              type="email"
              placeholder="Email của bạn"
              style={{
                flex: 1,
                padding: '0.8rem',
                borderRadius: '4px',
                border: 'none',
                backgroundColor: '#333',
                color: 'white',
                '&:focus': {
                  outline: 'none',
                  boxShadow: '0 0 0 2px #4CAF50'
                }
              }}
            />
            <button type="submit" style={{
              padding: '0.8rem 1.5rem',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              '&:hover': {
                backgroundColor: '#45a049',
                transform: 'translateY(-2px)'
              }
            }}>
              Đăng Ký
            </button>
          </form>
        </div>
      </div>

      {/* Bottom bar */}
      <div style={{
        marginTop: '3rem',
        paddingTop: '2rem',
        borderTop: '1px solid #333',
        textAlign: 'center',
        color: '#999'
      }}>
        <p>
          © 2024 Shop Online. Tất cả quyền được bảo lưu.
        </p>
      </div>

      <style>
        {`
          @media (max-width: 768px) {
            .footer-content {
