import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Header from './components/Header';
import Hero from './components/Hero';
import Footer from './components/Footer';
import ProductCard from './components/ProductCard';
import ProductDetail from './components/ProductDetail';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Auth from './components/Auth';
import Account from './components/Account';
import Orders from './components/Orders';
import Search from './components/Search';
import Review from './components/Review';
import Admin from './components/Admin';

// Global styles
const globalStyles = `
  @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;500;700&display=swap');

  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Roboto', sans-serif;
  }

  :root {
    --primary-color: #2563eb;
    --primary-hover: #1d4ed8;
    --secondary-color: #10b981;
    --secondary-hover: #059669;
    --text-color: #1f2937;
    --text-light: #6b7280;
    --background-light: #f3f4f6;
    --white: #ffffff;
    --danger: #ef4444;
    --danger-hover: #dc2626;
    --success: #22c55e;
    --success-hover: #16a34a;
    --border-color: #e5e7eb;
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  }

  body {
    background-color: var(--background-light);
    color: var(--text-color);
    line-height: 1.5;
  }

  h1, h2, h3, h4, h5, h6 {
    font-weight: 600;
    line-height: 1.25;
  }

  button, input, select, textarea {
    font-family: inherit;
  }

  a {
    color: var(--primary-color);
    text-decoration: none;
    transition: color 0.2s ease;
  }

  a:hover {
    color: var(--primary-hover);
  }
`;

// Protected Route component
const ProtectedRoute = ({ children, allowedRoles }) => {
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
  const userRole = localStorage.getItem('userRole');

  if (!isLoggedIn) {
    return <Navigate to="/auth" />;
  }

  if (allowedRoles && !allowedRoles.includes(userRole)) {
    return <Navigate to="/" />;
  }

  return children;
};

const products = [
  {
    id: 1,
    name: 'Điện thoại iPhone 13',
    description: 'iPhone 13 128GB - Hàng chính hãng',
    price: 19990000,
    oldPrice: 22990000,
    image: 'https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-blue-1-600x600.jpg',
    category: 'Điện thoại',
    isNew: true,
    isSale: true
  },
  {
    id: 2,
    name: 'Laptop Dell XPS 13',
    description: 'Laptop Dell XPS 13 9310 - Core i7',
    price: 32990000,
    oldPrice: 35990000,
    image: 'https://images.fpt.shop/unsafe/fit-in/800x800/filters:quality(90):fill(white)/fptshop.com.vn/Uploads/Originals/2021/9/15/637673978199600673_dell-xps-13-9310-i7-1165g7-16gb-512gb-4k-touch-win10-bac-1.png',
    category: 'Laptop',
    isNew: true,
    isSale: true
  },
  {
    id: 3,
    name: 'Tai nghe AirPods Pro',
    description: 'Tai nghe Apple AirPods Pro - Hàng chính hãng',
    price: 5990000,
    oldPrice: 6990000,
    image: 'https://cdn.tgdd.vn/Products/Images/54/211032/airpods-pro-2-thumb-600x600.jpg',
    category: 'Phụ kiện',
    isNew: false,
    isSale: true
  },
  {
    id: 4,
    name: 'Samsung Galaxy S21',
    description: 'Samsung Galaxy S21 5G 128GB',
    price: 16990000,
    oldPrice: 19990000,
    image: 'https://cdn.tgdd.vn/Products/Images/42/226316/samsung-galaxy-s21-tim-600x600.jpg',
    category: 'Điện thoại',
    isNew: false,
    isSale: true
  },
  {
    id: 5,
    name: 'MacBook Pro M1',
    description: 'MacBook Pro M1 13 inch 256GB',
    price: 27990000,
    oldPrice: 29990000,
    image: 'https://cdn.tgdd.vn/Products/Images/44/231244/macbook-pro-m1-2020-16gb-600x600.jpg',
    category: 'Laptop',
    isNew: true,
    isSale: false
  },
  {
    id: 6,
    name: 'Apple Watch Series 7',
    description: 'Apple Watch Series 7 GPS 45mm',
    price: 10990000,
    oldPrice: 12990000,
    image: 'https://cdn.tgdd.vn/Products/Images/7077/250728/apple-watch-s7-45mm-midnight-aluminum-midnight-sport-band-600x600.jpg',
    category: 'Phụ kiện',
    isNew: true,
    isSale: true
  }
];

const categories = [
  {
    id: 1,
    name: 'Điện thoại',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-phone.png',
    count: 2
  },
  {
    id: 2,
    name: 'Laptop',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-laptop.png',
    count: 2
  },
  {
    id: 3,
    name: 'Phụ kiện',
    image: 'https://cdn.tgdd.vn/Category/5692/thumb/icon-tablet.png',
    count: 2
  }
];

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);

  // Hàm kiểm tra trạng thái đăng nhập
  const checkLoginStatus = () => {
    const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
    const role = localStorage.getItem('userRole');
    setIsLoggedIn(loggedIn);
    setUserRole(role);
  };

  // Kiểm tra trạng thái đăng nhập khi component mount
  useEffect(() => {
    checkLoginStatus();
  }, []);

  // Lắng nghe sự kiện storage để cập nhật trạng thái đăng nhập
  useEffect(() => {
    const handleStorageChange = () => {
      checkLoginStatus();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  const addToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
      setCartItems(cartItems.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCartItems(cartItems.filter(item => item.id !== productId));
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Router>
      <style>{globalStyles}</style>
      <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <Header 
          cartCount={cartItems.length} 
          isLoggedIn={isLoggedIn} 
          userRole={userRole}
          onLogout={checkLoginStatus} 
        />
        <main style={{ flex: 1 }}>
          <Routes>
            {/* Public routes */}
            <Route path="/auth" element={<Auth onLogin={checkLoginStatus} />} />
            <Route path="/search" element={<Search products={products} />} />
            <Route path="/" element={
              <>
                <Hero />
                <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
                  {/* Flash Sale Section */}
                  <div style={{ marginBottom: '4rem' }}>
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      marginBottom: '2rem'
                    }}>
                      <h2 style={{
                        fontSize: '2rem',
                        color: 'var(--text-color)',
                        fontWeight: '600',
                        display: 'flex',
                        alignItems: 'center',
                        gap: '1rem'
                      }}>
                        <span style={{
                          backgroundColor: 'var(--danger)',
                          color: 'var(--white)',
                          padding: '0.5rem 1rem',
                          borderRadius: '8px',
                          fontSize: '1.5rem'
                        }}>
                          FLASH SALE
                        </span>
                        <span style={{ color: 'var(--danger)' }}>Kết thúc sau: 23:59:59</span>
                      </h2>
                      <Link
                        to="/search?type=sale"
                        style={{
                          color: 'var(--primary-color)',
                          textDecoration: 'none',
                          fontWeight: '500',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '0.5rem'
                        }}
                      >
                        Xem tất cả
                        <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
                          <path d="M7.5 15L12.5 10L7.5 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                      </Link>
                    </div>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
                      gap: '2rem'
                    }}>
                      {products.filter(p => p.isSale).map(product => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          addToCart={addToCart}
                        />
                      ))}
                    </div>
                  </div>

                  {/* Categories Section */}
                  <div style={{ marginBottom: '4rem' }}>
                    <h2 style={{
                      fontSize: '2rem',
                      color: 'var(--text-color)',
                      fontWeight: '600',
                      marginBottom: '2rem'
                    }}>
                      Danh Mục Sản Phẩm
                    </h2>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
                      gap: '2rem'
                    }}>
                      {categories.map(category => (
                        <Link
                          key={category.id}
                          to={`/search?category=${category.name}`}
                          style={{
                            textDecoration: 'none',
                            color: 'var(--text-color)',
                            backgroundColor: 'var(--white)',
                            borderRadius: '12px',
                            padding: '2rem',
                            textAlign: 'center',
                            boxShadow: 'var(--shadow)',
                            transition: 'all 0.2s ease',
                            '&:hover': {
                              transform: 'translateY(-4px)',
                              boxShadow: 'var(--shadow-md)'
                            }
                          }}
                        >
                          <img
                            src={category.image}
                            alt={category.name}
                            style={{
                              width: '80px',
                              height: '80px',
                              marginBottom: '1rem'
                            }}
                          />
                          <h3 style={{
                            fontSize: '1.25rem',
                            fontWeight: '500',
                            marginBottom: '0.5rem'
                          }}>
                            {category.name}
                          </h3>
                          <p style={{
                            color: 'var(--text-light)',
                            fontSize: '0.875rem'
                          }}>
                            {category.count} sản phẩm
                          </p>
                        </Link>
                      ))}
                    </div>
                  </div>

                  {/* New Products Section */}
                  <div style={{ marginBottom: '4rem' }}>
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      marginBottom: '2rem'
                    }}>
                      <h2 style={{
                        fontSize: '2rem',
                        color: 'var(--text-color)',
                        fontWeight: '600'
                      }}>
                        Sản Phẩm Mới
                      </h2>
                      <Link
                        to="/search?type=new"
                        style={{
                          color: 'var(--primary-color)',
                          textDecoration: 'none',
                          fontWeight: '500',
                          display: 'flex',
                          alignItems: 'center',
                          gap: '0.5rem'
                        }}
                      >
                        Xem tất cả
                        <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
                          <path d="M7.5 15L12.5 10L7.5 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                      </Link>
                    </div>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
                      gap: '2rem'
                    }}>
                      {products.filter(p => p.isNew).map(product => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          addToCart={addToCart}
                        />
                      ))}
                    </div>
                  </div>

                  {/* Featured Products Section */}
                  <div>
                    <h2 style={{
                      fontSize: '2rem',
                      color: 'var(--text-color)',
                      fontWeight: '600',
                      marginBottom: '2rem'
                    }}>
                      Sản Phẩm Nổi Bật
                    </h2>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
                      gap: '2rem'
                    }}>
                      {products.map(product => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          addToCart={addToCart}
                        />
                      ))}
                    </div>
                  </div>
                </div>
              </>
            } />

            {/* Protected user routes */}
            <Route 
              path="/product/:id" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <ProductDetail products={products} addToCart={addToCart} />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/cart" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/checkout" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Checkout cartItems={cartItems} total={total} />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/account" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Account />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/orders" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Orders />
                </ProtectedRoute>
              } 
            />

            <Route 
              path="/product/:id/reviews" 
              element={
                <ProtectedRoute allowedRoles={['user', 'admin']}>
                  <Review />
                </ProtectedRoute>
              } 
            />

            {/* Admin routes */}
            <Route 
              path="/admin" 
              element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <Admin />
                </ProtectedRoute>
              } 
            />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
