import React, { useState } from 'react';
import { FaUser, FaLock, FaHistory, FaEdit, FaSave } from 'react-icons/fa';

function Account() {
  const [isEditing, setIsEditing] = useState(false);
  const [userInfo, setUserInfo] = useState({
    name: 'Nguyễn Văn A',
    email: 'nguyenvana@example.com',
    phone: '0123456789',
    address: '123 Đường ABC, Quận XYZ, TP.HCM'
  });

  const [password, setPassword] = useState({
    current: '',
    new: '',
    confirm: ''
  });

  const handleInfoChange = (e) => {
    const { name, value } = e.target;
    setUserInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPassword(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSaveInfo = () => {
    // TODO: Implement save user info logic
    setIsEditing(false);
  };

  const handleChangePassword = () => {
    // TODO: Implement change password logic
    setPassword({ current: '', new: '', confirm: '' });
  };

  return (
    <div style={{
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '2rem',
      minHeight: '100vh'
    }}>
      <h1 style={{
        fontSize: '2rem',
        marginBottom: '2rem',
        color: '#333',
        textAlign: 'center'
      }}>
        Tài Khoản Của Tôi
      </h1>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '2rem'
      }}>
        {/* Thông tin cá nhân */}
        <div style={{
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            marginBottom: '1.5rem'
          }}>
            <FaUser style={{ fontSize: '1.5rem', color: '#4CAF50', marginRight: '1rem' }} />
            <h2 style={{ fontSize: '1.5rem', color: '#333' }}>Thông Tin Cá Nhân</h2>
          </div>

          {isEditing ? (
            <form onSubmit={(e) => { e.preventDefault(); handleSaveInfo(); }}>
              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                  Họ và tên
                </label>
                <input
                  type="text"
                  name="name"
                  value={userInfo.name}
                  onChange={handleInfoChange}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    borderRadius: '4px',
                    border: '1px solid #ddd',
                    fontSize: '1rem'
                  }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  value={userInfo.email}
                  onChange={handleInfoChange}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    borderRadius: '4px',
                    border: '1px solid #ddd',
                    fontSize: '1rem'
                  }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                  Số điện thoại
                </label>
                <input
                  type="tel"
                  name="phone"
                  value={userInfo.phone}
                  onChange={handleInfoChange}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    borderRadius: '4px',
                    border: '1px solid #ddd',
                    fontSize: '1rem'
                  }}
                />
              </div>

              <div style={{ marginBottom: '1.5rem' }}>
                <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                  Địa chỉ
                </label>
                <textarea
                  name="address"
                  value={userInfo.address}
                  onChange={handleInfoChange}
                  style={{
                    width: '100%',
                    padding: '0.8rem',
                    borderRadius: '4px',
                    border: '1px solid #ddd',
                    fontSize: '1rem',
                    minHeight: '100px'
                  }}
                />
              </div>

              <button
                type="submit"
                style={{
                  backgroundColor: '#4CAF50',
                  color: 'white',
                  padding: '0.8rem 1.5rem',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '1rem',
                  transition: 'all 0.3s ease'
                }}
              >
                <FaSave /> Lưu thay đổi
              </button>
            </form>
          ) : (
            <>
              <div style={{ marginBottom: '1rem' }}>
                <label style={{ color: '#666' }}>Họ và tên:</label>
                <p style={{ marginTop: '0.5rem' }}>{userInfo.name}</p>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label style={{ color: '#666' }}>Email:</label>
                <p style={{ marginTop: '0.5rem' }}>{userInfo.email}</p>
              </div>
              <div style={{ marginBottom: '1rem' }}>
                <label style={{ color: '#666' }}>Số điện thoại:</label>
                <p style={{ marginTop: '0.5rem' }}>{userInfo.phone}</p>
              </div>
              <div style={{ marginBottom: '1.5rem' }}>
                <label style={{ color: '#666' }}>Địa chỉ:</label>
                <p style={{ marginTop: '0.5rem' }}>{userInfo.address}</p>
              </div>
              <button
                onClick={() => setIsEditing(true)}
                style={{
                  backgroundColor: '#2196F3',
                  color: 'white',
                  padding: '0.8rem 1.5rem',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '1rem',
                  transition: 'all 0.3s ease'
                }}
              >
                <FaEdit /> Chỉnh sửa
              </button>
            </>
          )}
        </div>

        {/* Đổi mật khẩu */}
        <div style={{
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            marginBottom: '1.5rem'
          }}>
            <FaLock style={{ fontSize: '1.5rem', color: '#4CAF50', marginRight: '1rem' }} />
            <h2 style={{ fontSize: '1.5rem', color: '#333' }}>Đổi Mật Khẩu</h2>
          </div>

          <form onSubmit={(e) => { e.preventDefault(); handleChangePassword(); }}>
            <div style={{ marginBottom: '1rem' }}>
              <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                Mật khẩu hiện tại
              </label>
              <input
                type="password"
                name="current"
                value={password.current}
                onChange={handlePasswordChange}
                style={{
                  width: '100%',
                  padding: '0.8rem',
                  borderRadius: '4px',
                  border: '1px solid #ddd',
                  fontSize: '1rem'
                }}
              />
            </div>

            <div style={{ marginBottom: '1rem' }}>
              <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                Mật khẩu mới
              </label>
              <input
                type="password"
                name="new"
                value={password.new}
                onChange={handlePasswordChange}
                style={{
                  width: '100%',
                  padding: '0.8rem',
                  borderRadius: '4px',
                  border: '1px solid #ddd',
                  fontSize: '1rem'
                }}
              />
            </div>

            <div style={{ marginBottom: '1.5rem' }}>
              <label style={{ display: 'block', marginBottom: '0.5rem', color: '#666' }}>
                Xác nhận mật khẩu mới
              </label>
              <input
                type="password"
                name="confirm"
                value={password.confirm}
                onChange={handlePasswordChange}
                style={{
                  width: '100%',
                  padding: '0.8rem',
                  borderRadius: '4px',
                  border: '1px solid #ddd',
                  fontSize: '1rem'
                }}
              />
            </div>

            <button
              type="submit"
              style={{
                backgroundColor: '#4CAF50',
                color: 'white',
                padding: '0.8rem 1.5rem',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '1rem',
                transition: 'all 0.3s ease'
              }}
            >
              Đổi mật khẩu
            </button>
          </form>
        </div>

        {/* Lịch sử đơn hàng */}
        <div style={{
          backgroundColor: 'white',
          borderRadius: '12px',
          padding: '2rem',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            marginBottom: '1.5rem'
          }}>
            <FaHistory style={{ fontSize: '1.5rem', color: '#4CAF50', marginRight: '1rem' }} />
            <h2 style={{ fontSize: '1.5rem', color: '#333' }}>Lịch Sử Đơn Hàng</h2>
          </div>

          <div style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem'
          }}>
            {/* Sample order history */}
            <div style={{
              padding: '1rem',
              border: '1px solid #ddd',
              borderRadius: '8px',
              transition: 'all 0.3s ease',
              cursor: 'pointer',
              '&:hover': {
                transform: 'translateY(-2px)',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
              }
            }}>
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                marginBottom: '0.5rem'
              }}>
                <span style={{ fontWeight: 'bold' }}>Đơn hàng #12345</span>
                <span style={{ color: '#4CAF50' }}>Đã giao</span>
              </div>
              <div style={{ color: '#666' }}>
                <p>Ngày đặt: 01/01/2024</p>
                <p>Tổng tiền: 2,500,000đ</p>
              </div>
            </div>

            <div style={{
              padding: '1rem',
              border: '1px solid #ddd',
              borderRadius: '8px',
              transition: 'all 0.3s ease',
              cursor: 'pointer',
              '&:hover': {
                transform: 'translateY(-2px)',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
              }
            }}>
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                marginBottom: '0.5rem'
              }}>
                <span style={{ fontWeight: 'bold' }}>Đơn hàng #12344</span>
                <span style={{ color: '#FFA500' }}>Đang xử lý</span>
              </div>
              <div style={{ color: '#666' }}>
                <p>Ngày đặt: 31/12/2023</p>
                <p>Tổng tiền: 1,800,000đ</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <style>
        {`
          @media (max-width: 768px) {
            .account-grid {
              grid-template-columns: 1fr;
            }
          }
        `}
      </style>
    </div>
  );
}

export default Account;
