import React from 'react';
import ProductCard from './ProductCard';

const Home = ({ products, onAddToCart }) => (
  <>
    <style>{`
      .hero-section {
        background: linear-gradient(135deg, #6366f1 0%, #60a5fa 100%);
        color: white;
        padding: 64px 0 48px 0;
        text-align: center;
        position: relative;
        overflow: hidden;
      }
      .hero-section h1 {
        font-size: 3rem;
        font-weight: 800;
        margin-bottom: 1rem;
        letter-spacing: -1px;
      }
      .hero-section p {
        font-size: 1.25rem;
        margin-bottom: 2rem;
        opacity: 0.95;
      }
      .hero-btn {
        background: linear-gradient(90deg, #fbbf24 0%, #f59e42 100%);
        color: #222;
        font-weight: 700;
        padding: 0.85rem 2.5rem;
        border-radius: 999px;
        font-size: 1.1rem;
        border: none;
        box-shadow: 0 4px 24px rgba(0,0,0,0.08);
        cursor: pointer;
        transition: background 0.2s, transform 0.2s;
      }
      .hero-btn:hover {
        background: linear-gradient(90deg, #f59e42 0%, #fbbf24 100%);
        transform: translateY(-2px) scale(1.04);
      }
      .products-section {
        max-width: 1200px;
        margin: 0 auto;
        padding: 48px 16px 32px 16px;
      }
      .products-title {
        font-size: 2rem;
        font-weight: 700;
        margin-bottom: 2rem;
        color: #222;
        text-align: center;
      }
      .products-grid {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
        gap: 2rem;
      }
      .footer-section {
        background: linear-gradient(90deg, #6366f1 0%, #60a5fa 100%);
        color: white;
        padding: 48px 0 24px 0;
        text-align: center;
        margin-top: 48px;
        position: relative;
      }
      .footer-section .footer-title {
        font-size: 1.5rem;
        font-weight: 700;
        margin-bottom: 1rem;
      }
      .footer-links {
        display: flex;
        justify-content: center;
        gap: 2rem;
        margin-bottom: 1.5rem;
        flex-wrap: wrap;
      }
      .footer-link {
        color: #fbbf24;
        text-decoration: none;
        font-weight: 500;
        transition: color 0.2s;
      }
      .footer-link:hover {
        color: #fff;
        text-decoration: underline;
      }
      .footer-socials {
        margin-bottom: 1.5rem;
      }
      .footer-socials a {
        display: inline-block;
        margin: 0 0.5rem;
        color: #fff;
        font-size: 1.5rem;
        transition: color 0.2s, transform 0.2s;
      }
      .footer-socials a:hover {
        color: #fbbf24;
        transform: scale(1.15);
      }
      .footer-copyright {
        font-size: 0.95rem;
        opacity: 0.8;
      }
      @media (max-width: 600px) {
        .hero-section h1 { font-size: 2rem; }
        .products-title { font-size: 1.3rem; }
        .footer-section .footer-title { font-size: 1.1rem; }
      }
    `}</style>
    <section className="hero-section">
      <h1>Chào mừng đến với Siêu Thị Online!</h1>
      <p>Khám phá hàng ngàn sản phẩm công nghệ, điện tử, phụ kiện chính hãng với giá siêu tốt. Giao hàng nhanh, thanh toán tiện lợi!</p>
      <a href="#products" className="hero-btn">Khám phá ngay</a>
    </section>
    <section className="products-section" id="products">
      <div className="products-title">Sản phẩm nổi bật</div>
      <div className="products-grid">
        {products.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={onAddToCart}
          />
        ))}
      </div>
    </section>
    <footer className="footer-section">
      <div className="footer-title">Siêu Thị Online</div>
      <div className="footer-links">
        <a href="#" className="footer-link">Về chúng tôi</a>
        <a href="#" className="footer-link">Chính sách bảo mật</a>
        <a href="#" className="footer-link">Hỗ trợ khách hàng</a>
        <a href="#" className="footer-link">Liên hệ</a>
      </div>
      <div className="footer-socials">
        <a href="https://facebook.com" target="_blank" rel="noopener noreferrer"><i className="fab fa-facebook"></i></a>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer"><i className="fab fa-instagram"></i></a>
        <a href="https://tiktok.com" target="_blank" rel="noopener noreferrer"><i className="fab fa-tiktok"></i></a>
        <a href="mailto:support@sieuthionline.vn"><i className="fas fa-envelope"></i></a>
      </div>
      <div className="footer-copyright">
        &copy; {new Date().getFullYear()} Siêu Thị Online. All rights reserved.
      </div>
      {/* FontAwesome CDN for icons */}
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css" />
    </footer>
  </>
);

export default Home;
