import React from 'react';
import { Link } from 'react-router-dom';
import { FaShoppingCart, FaUser, FaClipboardList, FaSearch, FaCog } from 'react-icons/fa';

const menuItemStyle = {
  textDecoration: 'none',
  color: '#333',
  display: 'flex',
  alignItems: 'center',
  gap: '0.5rem',
  padding: '0.5rem 1rem',
  borderRadius: '8px',
  transition: 'all 0.3s ease',
  position: 'relative',
  overflow: 'hidden',
  '&:hover': {
    backgroundColor: '#f0f0f0',
    transform: 'translateY(-2px)',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
  }
};

const cartBadgeStyle = {
  position: 'absolute',
  top: '-8px',
  right: '-8px',
  backgroundColor: '#4CAF50',
  color: 'white',
  borderRadius: '50%',
  width: '20px',
  height: '20px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  fontSize: '0.75rem',
  animation: 'bounce 0.5s ease'
};

function Header({ cartCount }) {
  return (
    <header style={{
      background: 'linear-gradient(to right, #ffffff, #f8f9fa)',
      padding: '1rem 2rem',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      backdropFilter: 'blur(10px)',
      WebkitBackdropFilter: 'blur(10px)'
    }}>
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <Link to="/" style={{
          textDecoration: 'none',
          color: '#333',
          fontSize: '1.8rem',
          fontWeight: 'bold',
          background: 'linear-gradient(45deg, #2196F3, #4CAF50)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          transition: 'transform 0.3s ease',
          '&:hover': {
            transform: 'scale(1.05)'
          }
        }}>
          Shop Online
        </Link>

        <div style={{ 
          display: 'flex', 
          gap: '1.5rem', 
          alignItems: 'center',
          '@media (max-width: 768px)': {
            gap: '1rem'
          }
        }}>
          <Link to="/search" style={{
            ...menuItemStyle,
            '&:hover': {
              backgroundColor: '#e3f2fd',
              color: '#2196F3'
            }
          }}>
            <FaSearch style={{ transition: 'transform 0.3s ease' }} />
            <span style={{ transition: 'all 0.3s ease' }}>Tìm kiếm</span>
          </Link>
          <Link to="/account" style={{
            ...menuItemStyle,
            '&:hover': {
              backgroundColor: '#e8f5e9',
              color: '#4CAF50'
            }
          }}>
            <FaUser style={{ transition: 'transform 0.3s ease' }} />
            <span style={{ transition: 'all 0.3s ease' }}>Tài khoản</span>
          </Link>
          <Link to="/orders" style={{
            ...menuItemStyle,
            '&:hover': {
              backgroundColor: '#fff3e0',
              color: '#FF9800'
            }
          }}>
            <FaClipboardList style={{ transition: 'transform 0.3s ease' }} />
            <span style={{ transition: 'all 0.3s ease' }}>Đơn hàng</span>
          </Link>
          <Link to="/admin" style={{
            ...menuItemStyle,
            '&:hover': {
              backgroundColor: '#f3e5f5',
              color: '#9C27B0'
            }
          }}>
            <FaCog style={{ transition: 'transform 0.3s ease' }} />
            <span style={{ transition: 'all 0.3s ease' }}>Quản lý</span>
          </Link>
          <Link to="/cart" style={{
            ...menuItemStyle,
            '&:hover': {
              backgroundColor: '#e8f5e9',
              color: '#4CAF50'
            }
          }}>
            <FaShoppingCart style={{ transition: 'transform 0.3s ease' }} />
            <span style={{ transition: 'all 0.3s ease' }}>Giỏ hàng</span>
            {cartCount > 0 && (
              <span style={cartBadgeStyle}>
                {cartCount}
              </span>
            )}
          </Link>
        </div>
      </nav>

      <style>
        {`
          @keyframes bounce {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.2); }
          }

          a:hover svg {
            transform: scale(1.2);
          }

          a:hover span {
            transform: translateX(2px);
          }

          @media (max-width: 768px) {
            nav {
              flex-direction: column;
              gap: 1rem;
            }
            
            .menu-items {
              flex-wrap: wrap;
              justify-content: center;
            }
          }
        `}
      </style>
    </header>
  );
}

export default Header;
