import React from 'react';
import { Link } from 'react-router-dom';
import { FaShoppingCart, FaUser, FaClipboardList } from 'react-icons/fa';

function Header({ cartCount }) {
  return (
    <header style={{
      backgroundColor: '#333',
      color: 'white',
      padding: '1rem',
      position: 'sticky',
      top: 0,
      zIndex: 1000
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <Link to="/" style={{ color: 'white', textDecoration: 'none', fontSize: '1.5rem', fontWeight: 'bold' }}>
          Shop Online
        </Link>

        <nav style={{ display: 'flex', gap: '2rem' }}>
          <Link
            to="/account"
            style={{
              color: 'white',
              textDecoration: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}
          >
            <FaUser /> Tài khoản
          </Link>
          <Link
            to="/orders"
            style={{
              color: 'white',
              textDecoration: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}
          >
            <FaClipboardList /> Đơn hàng
          </Link>
          <Link
            to="/cart"
            style={{
              color: 'white',
              textDecoration: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}
          >
            <FaShoppingCart /> Giỏ hàng
            {cartCount > 0 && (
              <span style={{
                backgroundColor: '#e44d26',
                color: 'white',
                borderRadius: '50%',
                padding: '0.2rem 0.5rem',
                fontSize: '0.8rem'
              }}>
                {cartCount}
              </span>
            )}
          </Link>
        </nav>
      </div>
    </header>
  );
}

export default Header;
