import React from 'react';
import { Link } from 'react-router-dom';
import { FaShoppingCart, FaHeart, FaRegHeart } from 'react-icons/fa';

function ProductCard({ product, addToCart }) {
  const [isHovered, setIsHovered] = React.useState(false);
  const [isWishlist, setIsWishlist] = React.useState(false);

  const discount = product.oldPrice ? Math.round(((product.oldPrice - product.price) / product.oldPrice) * 100) : 0;

  return (
    <div
      className="card"
      style={{
        position: 'relative',
        overflow: 'hidden',
        backgroundColor: 'var(--white)',
        borderRadius: '12px',
        transition: 'all 0.3s ease',
        cursor: 'pointer',
        boxShadow: 'var(--shadow)',
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: 'var(--shadow-lg)'
        }
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Badges */}
      {product.isNew && (
        <div className="badge badge-new" style={{
          position: 'absolute',
          top: '8px',
          left: '8px',
          padding: '0.25rem 0.75rem',
          backgroundColor: 'var(--primary-color)',
          color: 'var(--white)',
          borderRadius: '12px',
          fontSize: '0.75rem',
          fontWeight: '500',
          zIndex: 2,
          boxShadow: 'var(--shadow)'
        }}>
          Mới
        </div>
      )}
      {product.isSale && (
        <div className="badge badge-sale" style={{
          position: 'absolute',
          top: '8px',
          right: '8px',
          padding: '0.25rem 0.75rem',
          backgroundColor: 'var(--danger)',
          color: 'var(--white)',
          borderRadius: '12px',
          fontSize: '0.75rem',
          fontWeight: '500',
          zIndex: 2,
          boxShadow: 'var(--shadow)'
        }}>
          -{discount}%
        </div>
      )}

      {/* Wishlist Button */}
      <button
        onClick={(e) => {
          e.preventDefault();
          setIsWishlist(!isWishlist);
        }}
        style={{
          position: 'absolute',
          top: '8px',
          right: product.isSale ? '70px' : '8px',
          background: 'var(--white)',
          border: 'none',
          borderRadius: '50%',
          width: '32px',
          height: '32px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          cursor: 'pointer',
          boxShadow: 'var(--shadow)',
          transition: 'all 0.2s ease',
          zIndex: 2,
          transform: isHovered ? 'translateX(0)' : 'translateX(60px)',
          '&:hover': {
            background: 'var(--danger)',
            color: 'var(--white)'
          }
        }}
      >
        {isWishlist ? <FaHeart color="var(--danger)" /> : <FaRegHeart />}
      </button>

      {/* Product Image */}
      <Link to={`/product/${product.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
        <div style={{
          position: 'relative',
          paddingTop: '100%',
          overflow: 'hidden',
          borderRadius: '12px 12px 0 0',
          backgroundColor: 'var(--bg-light)'
        }}>
          <img
            src={product.image}
            alt={product.name}
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              transition: 'transform 0.5s ease',
              transform: isHovered ? 'scale(1.1)' : 'scale(1)'
            }}
          />
        </div>

        {/* Product Info */}
        <div style={{
          padding: '1rem',
          backgroundColor: 'var(--white)',
          flex: 1,
          display: 'flex',
          flexDirection: 'column'
        }}>
          <h3 style={{
            fontSize: '0.875rem',
            fontWeight: '600',
            marginBottom: '0.5rem',
            color: 'var(--text-color)',
            display: '-webkit-box',
            WebkitLineClamp: 2,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            height: '2.8em',
            lineHeight: '1.4'
          }}>
            {product.name}
          </h3>

          <p style={{
            fontSize: '0.75rem',
            color: 'var(--text-light)',
            marginBottom: '0.75rem',
            display: '-webkit-box',
            WebkitLineClamp: 2,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            height: '2.4em',
            lineHeight: '1.4'
          }}>
            {product.description}
          </p>

          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            marginTop: 'auto'
          }}>
            <div className="price" style={{
              display: 'flex',
              alignItems: 'baseline',
              gap: '0.5rem'
            }}>
              {product.oldPrice && (
                <span style={{
                  fontSize: '0.75rem',
                  color: 'var(--text-light)',
                  textDecoration: 'line-through'
                }}>
                  {product.oldPrice.toLocaleString('vi-VN')}đ
                </span>
              )}
              <span style={{
                fontSize: '1rem',
                fontWeight: '700',
                color: 'var(--primary-color)'
              }}>
                {product.price.toLocaleString('vi-VN')}đ
              </span>
            </div>
          </div>

          {/* Add to Cart Button */}
          <button
            onClick={(e) => {
              e.preventDefault();
              addToCart(product);
            }}
            style={{
              width: '100%',
              padding: '0.5rem',
              backgroundColor: 'var(--primary-color)',
              color: 'var(--white)',
              border: 'none',
              borderRadius: '8px',
              fontSize: '0.75rem',
              fontWeight: '500',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '0.5rem',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
              transform: isHovered ? 'translateY(0)' : 'translateY(100%)',
              opacity: isHovered ? 1 : 0,
              position: 'absolute',
              bottom: '1rem',
              left: '1rem',
              right: '1rem',
              '&:hover': {
                backgroundColor: 'var(--primary-hover)',
                transform: 'translateY(-2px)'
              }
            }}
          >
            <FaShoppingCart />
            Thêm vào giỏ
          </button>
        </div>
      </Link>
    </div>
  );
}

export default ProductCard;
