import React from 'react';

const products = [
  {
    id: 1,
    name: 'iPhone 15 Pro Max',
    price: 29990000,
    image: 'https://cdn.tgdd.vn/Products/Images/42/251192/iphone-15-pro-max-den-thumb-600x600.jpg',
    description: 'iPhone 15 Pro Max 256GB - Thiết kế titan cao cấp, chip A17 Pro mạnh mẽ'
  },
  {
    id: 2,
    name: 'Samsung Galaxy S24 Ultra',
    price: 27990000,
    image: 'https://cdn.tgdd.vn/Products/Images/42/299033/samsung-galaxy-s24-ultra-thumb-xanh-600x600.jpg',
    description: 'Samsung Galaxy S24 Ultra 256GB - Camera 200MP, chip Snapdragon 8 Gen 3'
  },
  {
    id: 3,
    name: 'MacBook Pro M3',
    price: 39990000,
    image: 'https://cdn.tgdd.vn/Products/Images/44/282885/macbook-pro-m3-14-inch-space-gray-thumb-600x600.jpg',
    description: 'MacBook Pro M3 14 inch - Chip M3 mạnh mẽ, màn hình Liquid Retina XDR'
  },
  {
    id: 4,
    name: 'iPad Pro M2',
    price: 24990000,
    image: 'https://cdn.tgdd.vn/Products/Images/522/251192/ipad-pro-m2-11-inch-wifi-128gb-thumb-600x600.jpg',
    description: 'iPad Pro M2 11 inch - Chip M2, màn hình Liquid Retina, hỗ trợ Apple Pencil 2'
  }
];

function ProductList({ addToCart }) {
  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
      <h2 style={{ fontSize: '2rem', fontWeight: 700, marginBottom: '2rem', color: '#1f2937' }}>
        Sản phẩm nổi bật
      </h2>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
        gap: '2rem'
      }}>
        {products.map(product => (
          <div key={product.id} style={{
            background: '#fff',
            borderRadius: '12px',
            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
            overflow: 'hidden',
            transition: 'transform 0.2s',
            ':hover': {
              transform: 'translateY(-4px)'
            }
          }}>
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: '100%',
                height: '200px',
                objectFit: 'cover'
              }}
            />
            <div style={{ padding: '1.5rem' }}>
              <h3 style={{ fontSize: '1.25rem', fontWeight: 600, marginBottom: '0.5rem', color: '#1f2937' }}>
                {product.name}
              </h3>
              <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
                {product.description}
              </p>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span style={{ fontSize: '1.25rem', fontWeight: 700, color: '#ef4444' }}>
                  {product.price.toLocaleString('vi-VN')}đ
                </span>
                <button
                  onClick={() => addToCart(product)}
                  style={{
                    background: '#3b82f6',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '0.5rem 1rem',
                    cursor: 'pointer',
                    transition: 'background-color 0.2s',
                    ':hover': {
                      background: '#2563eb'
                    }
                  }}
                >
                  Thêm vào giỏ
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
