import React, { useState, useEffect } from 'react';

const flashSaleProducts = [
  {
    id: 5,
    name: 'AirPods Pro 2',
    price: 5990000,
    originalPrice: 6990000,
    image: 'https://cdn.tgdd.vn/Products/Images/54/284266/airpods-pro-2-thumb-600x600.jpg',
    description: 'AirPods Pro 2 - Chống ồn chủ động, chế độ xuyên âm'
  },
  {
    id: 6,
    name: 'Apple Watch Series 9',
    price: 8990000,
    originalPrice: 9990000,
    image: 'https://cdn.tgdd.vn/Products/Images/7077/307923/apple-watch-series-9-gps-41mm-thumb-600x600.jpg',
    description: 'Apple Watch Series 9 - Chip S9, màn hình Always-On'
  },
  {
    id: 7,
    name: 'Samsung Galaxy Buds2 Pro',
    price: 3990000,
    originalPrice: 4990000,
    image: 'https://cdn.tgdd.vn/Products/Images/54/284266/samsung-galaxy-buds2-pro-thumb-600x600.jpg',
    description: 'Samsung Galaxy Buds2 Pro - Chống ồn chủ động, âm thanh 24-bit'
  }
];

function FlashSale({ addToCart }) {
  const [timeLeft, setTimeLeft] = useState({
    hours: 2,
    minutes: 0,
    seconds: 0
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(prevTime => {
        const totalSeconds = prevTime.hours * 3600 + prevTime.minutes * 60 + prevTime.seconds - 1;
        
        if (totalSeconds <= 0) {
          clearInterval(timer);
          return { hours: 0, minutes: 0, seconds: 0 };
        }

        return {
          hours: Math.floor(totalSeconds / 3600),
          minutes: Math.floor((totalSeconds % 3600) / 60),
          seconds: totalSeconds % 60
        };
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '2rem' }}>
        <h2 style={{ fontSize: '2rem', fontWeight: 700, color: '#1f2937' }}>
          Flash Sale
        </h2>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem',
          background: '#fee2e2',
          padding: '0.5rem 1rem',
          borderRadius: '6px'
        }}>
          <span style={{ color: '#ef4444', fontWeight: 600 }}>Kết thúc sau:</span>
          <div style={{
            background: '#ef4444',
            color: '#fff',
            padding: '0.25rem 0.5rem',
            borderRadius: '4px',
            fontWeight: 600
          }}>
            {String(timeLeft.hours).padStart(2, '0')}:{String(timeLeft.minutes).padStart(2, '0')}:{String(timeLeft.seconds).padStart(2, '0')}
          </div>
        </div>
      </div>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
        gap: '2rem'
      }}>
        {flashSaleProducts.map(product => (
          <div key={product.id} style={{
            background: '#fff',
            borderRadius: '12px',
            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
            overflow: 'hidden',
            transition: 'transform 0.2s',
            ':hover': {
              transform: 'translateY(-4px)'
            }
          }}>
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: '100%',
                height: '200px',
                objectFit: 'cover'
              }}
            />
            <div style={{ padding: '1.5rem' }}>
              <h3 style={{ fontSize: '1.25rem', fontWeight: 600, marginBottom: '0.5rem', color: '#1f2937' }}>
                {product.name}
              </h3>
              <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
                {product.description}
              </p>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <span style={{ fontSize: '1.25rem', fontWeight: 700, color: '#ef4444' }}>
                    {product.price.toLocaleString('vi-VN')}đ
                  </span>
                  <span style={{ color: '#9ca3af', textDecoration: 'line-through' }}>
                    {product.originalPrice.toLocaleString('vi-VN')}đ
                  </span>
                </div>
                <button
                  onClick={() => addToCart(product)}
                  style={{
                    background: '#ef4444',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '0.5rem 1rem',
                    cursor: 'pointer',
                    transition: 'background-color 0.2s',
                    ':hover': {
                      background: '#dc2626'
                    }
                  }}
                >
                  Mua ngay
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default FlashSale;
