import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import ProductDetail from './components/ProductDetail';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Auth from './components/Auth';
import './styles/global.css';
import './styles/components/product-card.css';
import './styles/components/auth.css';
import './styles/components/cart.css';
import './styles/components/checkout.css';
import './styles/components/product-detail.css';

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch products from API
    fetch('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error('Error fetching products:', err));
  }, []);

  const addToCart = (product) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, quantity) => {
    if (quantity < 1) return;
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId
          ? { ...item, quantity }
          : item
      )
    );
  };

  const handleLogin = (userData) => {
    setIsAuthenticated(true);
    setUser(userData);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUser(null);
  };

  return (
    <Router>
      <div className="app">
        {/* HEADER NAV */}
        <header style={{
          background: 'linear-gradient(90deg, #6366f1 0%, #60a5fa 100%)',
          color: '#fff',
          boxShadow: '0 4px 32px 0 rgba(99,102,241,0.10)',
          position: 'sticky',
          top: 0,
          zIndex: 1000
        }}>
          <div style={{
            maxWidth: '1200px',
            margin: '0 auto',
            padding: '1.2rem 2rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between'
          }}>
            <a href="/" style={{
              fontSize: '2rem',
              fontWeight: 900,
              letterSpacing: '-1px',
              color: '#fbbf24',
              textDecoration: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}>
              <i className="fas fa-store"></i> 
            </a>
            <nav style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
              <a href="/cart" style={{ color: '#fff', textDecoration: 'none', fontWeight: 500 }}>
                <i className="fas fa-shopping-cart"></i> Giỏ hàng
              </a>
              {isAuthenticated ? (
                <span style={{ fontWeight: 500 }}>
                  <i className="fas fa-user-circle"></i> {user?.name || 'Tài khoản'}
                  <button onClick={handleLogout} style={{
                    marginLeft: 12, background: '#fbbf24', color: '#222', border: 'none', borderRadius: 6, padding: '4px 12px', cursor: 'pointer'
                  }}>Đăng xuất</button>
                </span>
              ) : (
                <a href="/auth" style={{ color: '#fff', textDecoration: 'none', fontWeight: 500 }}>
                  <i className="fas fa-sign-in-alt"></i> Đăng nhập
                </a>
              )}
            </nav>
          </div>
          <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css" />
        </header>
        <main>
          <Routes>
            <Route path="/" element={
              <Home
                products={products}
                onAddToCart={addToCart}
                cart={cart}
                isAuthenticated={isAuthenticated}
                user={user}
                onLogout={handleLogout}
              />
            } />
            <Route path="/product/:id" element={
              <ProductDetail
                products={products}
                onAddToCart={addToCart}
              />
            } />
            <Route path="/cart" element={
              <Cart
                cart={cart}
